import copy


def get_max(array):
    temp_max = -1
    for i in range(1, len(array)):
        for j in range(len(array) - i - 1):
            temp_arr = swap_array(j, j + i, array)
            temp_max = max(temp_max, apply_function(temp_arr))
    return temp_max


def apply_function(arr):
    temp = 0
    for i in range(len(arr) - 1):
        temp += abs(arr[i] - arr[i + 1])
    return temp


def swap_array(start, end, arr):
    res = copy.deepcopy(arr)
    temp = arr[start:end + 1]
    res[start:end + 1] = temp[::-1]
    return res


